import React from 'react';

const generateMarkdown = (report) => {
  let markdown = `# Security Vulnerability Report for ${report.appName}\n\n`;
  markdown += `*   **Scan Date:** ${new Date().toUTCString()}\n`;
  markdown += `*   **Package Name:** ${report.packageName}\n---\n`;

  const severities = ['Critical', 'High', 'Medium'];
  severities.forEach(severity => {
    const vulnerabilities = report.vulnerabilities.filter(v => v.severity === severity);
    if (vulnerabilities.length > 0) {
      markdown += `## ${severity} Vulnerabilities\n\n`;
      vulnerabilities.forEach((vuln, index) => {
        markdown += `### ${index + 1}. ${vuln.title}\n`;
        markdown += `- **Severity:** \`${vuln.severity}\`\n`;
        markdown += `- **File:** \`${vuln.file}\`\n\n`;
        markdown += `#### Description\n${vuln.description}\n\n`;
        markdown += `#### Proof of Concept (PoC)\n${vuln.poc}\n\n`;
        markdown += `#### Mitigation Steps\n${vuln.mitigation}\n\n`;
        markdown += `#### Potential Impact\n${vuln.impact}\n\n---\n`;
      });
    }
  });
  return markdown;
};

const generateHTML = (report) => {
  const style = `
    body { font-family: sans-serif; padding: 20px; background: #f9f9f9; color: #333; }
    h1, h2, h3 { color: #2c3e50; }
    code, pre { background: #eee; padding: 4px 8px; border-radius: 4px; }
    .badge { display: inline-block; padding: 4px 10px; border-radius: 5px; font-weight: bold; }
    .critical { background: #d32f2f; color: #fff; }
    .high { background: #f57c00; color: #fff; }
    .medium { background: #fbc02d; color: #000; }
    .vuln { border-left: 4px solid #ccc; padding: 10px; margin: 15px 0; background: white; box-shadow: 0 0 10px rgba(0,0,0,0.05); }
  `;
  let html = `<html><head><meta charset="UTF-8"><title>${report.appName} - HTML Report</title><style>${style}</style></head><body>`;
  html += `<h1>Security Vulnerability Report for ${report.appName}</h1>`;
  html += `<p><strong>Scan Date:</strong> ${new Date().toUTCString()}</p>`;
  html += `<p><strong>Package Name:</strong> ${report.packageName}</p><hr/>`;

  ['Critical', 'High', 'Medium'].forEach(sev => {
    const list = report.vulnerabilities.filter(v => v.severity === sev);
    if (list.length) {
      html += `<h2>${sev} Vulnerabilities</h2>`;
      list.forEach((vuln, i) => {
        html += `
          <div class="vuln">
            <h3>${i + 1}. ${vuln.title} <span class="badge ${sev.toLowerCase()}">${sev}</span></h3>
            <p><strong>File:</strong> <code>${vuln.file}</code></p>
            <p><strong>Description:</strong> ${vuln.description}</p>
            <p><strong>Proof of Concept:</strong><pre>${vuln.poc}</pre></p>
            <p><strong>Mitigation:</strong><pre>${vuln.mitigation}</pre></p>
            <p><strong>Impact:</strong><pre>${vuln.impact}</pre></p>
          </div>
        `;
      });
    }
  });

  html += `</body></html>`;
  return html;
};

function ReportDashboard({ report }) {
  if (!report) return null;

  const downloadMarkdown = () => {
    const blob = new Blob([generateMarkdown(report)], { type: 'text/markdown' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `report-${report.appName}.md`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  const downloadHTML = () => {
    const blob = new Blob([generateHTML(report)], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `report-${report.appName}.html`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  const getSeverityStyles = (severity) => {
    switch (severity) {
      case 'Critical': return 'bg-red-600 text-white';
      case 'High': return 'bg-orange-500 text-white';
      case 'Medium': return 'bg-yellow-400 text-black';
      default: return 'bg-gray-300 text-black';
    }
  };

  return (
    <div className="max-w-5xl mx-auto p-6 mt-10 bg-white dark:bg-gray-900 shadow-2xl rounded-2xl border dark:border-gray-700">
      <div className="flex flex-col md:flex-row items-start md:items-center justify-between mb-6 gap-4">
        <h1 className="text-3xl font-bold text-gray-800 dark:text-white flex items-center gap-2">
          <span>🛡️</span> {report.appName} Report
        </h1>
        <div className="flex gap-3">
          <button onClick={downloadMarkdown} className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg text-sm">
            📄 Export Markdown
          </button>
          <button onClick={downloadHTML} className="px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg text-sm">
            🌐 Export HTML
          </button>
        </div>
      </div>

      <p className="text-gray-500 text-sm mb-1">📦 <strong>Package:</strong> {report.packageName}</p>
      <p className="text-gray-500 text-sm mb-6">🕒 <strong>Scan Date:</strong> {new Date().toLocaleString()}</p>

      <div className="space-y-6">
        {report.vulnerabilities.map((vuln, idx) => (
          <div key={idx} className="p-5 rounded-xl shadow border dark:border-gray-700 bg-gray-50 dark:bg-gray-800">
            <div className="flex items-center justify-between mb-3">
              <h3 className="text-lg font-semibold text-gray-800 dark:text-white">
                {idx + 1}. {vuln.title}
              </h3>
              <span className={`text-xs px-3 py-1 rounded-full font-bold ${getSeverityStyles(vuln.severity)}`}>
                {vuln.severity}
              </span>
            </div>
            <p className="text-sm text-gray-600 dark:text-gray-300 mb-2"><strong>File:</strong> <code className="bg-gray-200 dark:bg-gray-700 rounded px-2 py-0.5">{vuln.file}</code></p>
            <p className="text-gray-700 dark:text-gray-300 mb-3"><strong>Description:</strong> {vuln.description}</p>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
              <div>
                <p className="font-semibold mb-1 text-gray-800 dark:text-white">🧪 PoC:</p>
                <pre className="p-2 bg-black text-green-400 rounded h-32 overflow-auto whitespace-pre-wrap">{vuln.poc}</pre>
              </div>
              <div>
                <p className="font-semibold mb-1 text-gray-800 dark:text-white">🛠️ Mitigation:</p>
                <pre className="p-2 bg-gray-900 text-yellow-300 rounded h-32 overflow-auto whitespace-pre-wrap">{vuln.mitigation}</pre>
              </div>
              <div>
                <p className="font-semibold mb-1 text-gray-800 dark:text-white">⚠️ Impact:</p>
                <pre className="p-2 bg-red-100 dark:bg-red-900 text-red-700 dark:text-red-300 rounded h-32 overflow-auto whitespace-pre-wrap">{vuln.impact}</pre>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default ReportDashboard;
