import React from 'react';

// Helper to generate the markdown file content
const generateMarkdown = (report) => {
  let markdown = `# Security Vulnerability Report for ${report.appName}\n\n`;
  markdown += `*   **Scan Date:** ${new Date().toUTCString()}\n`;
  markdown += `*   **Package Name:** ${report.packageName}\n---\n`;

  const severities = ['Critical', 'High', 'Medium'];
  severities.forEach(severity => {
    const vulnerabilities = report.vulnerabilities.filter(v => v.severity === severity);
    if (vulnerabilities.length > 0) {
      markdown += `## ${severity} Vulnerabilities\n\n`;
      vulnerabilities.forEach((vuln, index) => {
        markdown += `### ${index + 1}. ${vuln.title}\n`;
        markdown += `- **Severity:** \`${vuln.severity}\`\n`;
        markdown += `- **File:** \`${vuln.file}\`\n\n`;
        markdown += `#### Description\n${vuln.description}\n\n`;
        markdown += `#### Proof of Concept (PoC)\n${vuln.poc}\n\n`;
        markdown += `#### Mitigation Steps\n${vuln.mitigation}\n\n`;
        markdown += `#### Potential Impact\n${vuln.impact}\n\n---\n`;
      });
    }
  });
  return markdown;
};

function ReportDashboard({ report }) {
  if (!report) return null;

  const downloadMarkdown = () => {
    const markdownContent = generateMarkdown(report);
    const blob = new Blob([markdownContent], { type: 'text/markdown' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `report-${report.appName}.md`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  const getSeverityColor = (severity) => {
    switch (severity) {
      case 'Critical':
        return 'bg-red-600 text-white';
      case 'High':
        return 'bg-orange-500 text-white';
      case 'Medium':
        return 'bg-yellow-400 text-black';
      default:
        return 'bg-gray-300 text-black';
    }
  };

  return (
    <div className="report-dashboard max-w-4xl mx-auto mt-10 p-6 bg-white dark:bg-gray-900 shadow-xl rounded-2xl">
      <div className="flex items-center justify-between mb-6">
        <h2 className="text-2xl font-semibold text-gray-800 dark:text-gray-200">
          ðŸ”’ Security Report: <span className="font-bold">{report.appName}</span>
        </h2>
        <button
          onClick={downloadMarkdown}
          className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg transition-all text-sm"
        >
          ðŸ“„ Export Markdown
        </button>
      </div>

      <p className="text-sm text-gray-500 mb-2">ðŸ“¦ Package: <strong>{report.packageName}</strong></p>
      <p className="text-sm text-gray-500 mb-6">ðŸ•’ Scan Date: {new Date().toLocaleString()}</p>

      <div className="space-y-4">
        {report.vulnerabilities.map((vuln, index) => (
          <details
            key={index}
            className={`border-l-4 p-4 rounded-lg shadow-md ${
              vuln.severity === 'Critical'
                ? 'border-red-600 bg-red-50'
                : vuln.severity === 'High'
                ? 'border-orange-500 bg-orange-50'
                : 'border-yellow-400 bg-yellow-50'
            }`}
          >
            <summary className="cursor-pointer font-semibold text-lg text-gray-800 flex items-center gap-2">
              <span className={`px-2 py-0.5 rounded-full text-xs font-semibold ${getSeverityColor(vuln.severity)}`}>
                {vuln.severity}
              </span>
              {vuln.title}
            </summary>

            <div className="mt-4 space-y-3 text-sm text-gray-700 dark:text-gray-200">
              <p><strong>Description:</strong> {vuln.description}</p>
              <p><strong>File:</strong> <code className="bg-gray-100 dark:bg-gray-800 px-1 rounded">{vuln.file}</code></p>

              <div>
                <strong>Proof of Concept (PoC):</strong>
                <pre className="bg-black text-green-400 p-3 rounded mt-1 overflow-auto">{vuln.poc}</pre>
              </div>

              <div>
                <strong>Mitigation Steps:</strong>
                <pre className="bg-gray-900 text-yellow-300 p-3 rounded mt-1 overflow-auto">{vuln.mitigation}</pre>
              </div>

              <div>
                <strong>Impact by Attackers:</strong>
                <pre className="bg-gray-100 dark:bg-gray-800 text-red-600 p-3 rounded mt-1 overflow-auto">{vuln.impact}</pre>
              </div>
            </div>
          </details>
        ))}
      </div>
    </div>
  );
}

export default ReportDashboard;
