import React, { useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';

function FileUpload({ setScanning, setReport }) {
  const onDrop = useCallback(acceptedFiles => {
    const file = acceptedFiles[0];
    if (file && file.name.endsWith('.apk')) {
      setScanning(true);
      setReport(null);

      const formData = new FormData();
      formData.append('apkfile', file);

      axios.post('http://localhost:5000/scan', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      }).then(response => {
        setReport(response.data);
      }).catch(error => {
        console.error("Error uploading file:", error);
        alert("Scan failed. See console for details.");
      }).finally(() => {
        setScanning(false);
      });
    } else {
      alert("Please upload a valid .apk file.");
    }
  }, [setScanning, setReport]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({ onDrop });

  return (
    <div
      {...getRootProps()}
      className="upload-box border-2 border-dashed border-gray-400 rounded-2xl p-8 text-center transition-all duration-300 ease-in-out hover:border-blue-500 hover:shadow-lg bg-white dark:bg-gray-800 dark:text-white"
    >
      <input {...getInputProps()} />
      <div className="flex flex-col items-center justify-center space-y-3">
        <svg
          className="w-12 h-12 text-gray-500 dark:text-gray-300"
          fill="none"
          stroke="currentColor"
          strokeWidth="1.5"
          viewBox="0 0 24 24"
        >
          <path strokeLinecap="round" strokeLinejoin="round" d="M12 16V4m0 0L8 8m4-4l4 4M4 16h16" />
        </svg>
        {
          isDragActive ?
            <p className="text-blue-600 font-medium">Drop the .apk file here ...</p> :
            <p className="text-gray-600 dark:text-gray-300">Drag & drop your <span className="font-semibold">.apk</span> file here, or click to select</p>
        }
        <p className="text-sm text-gray-400 dark:text-gray-500">Only .apk files are supported</p>
      </div>
    </div>
  );
}

export default FileUpload;
