import React from 'react';

// --- Helper Functions for Report Generation (Unchanged) ---
const generateMarkdown = (report) => {
  let markdown = `# Security Vulnerability Report for ${report.appName}\n\n`;
  markdown += `*   **Scan Date:** ${new Date().toUTCString()}\n`;
  markdown += `*   **Package Name:** ${report.packageName}\n---\n`;

  const severities = ['Critical', 'High', 'Medium'];
  severities.forEach(severity => {
    const vulnerabilities = report.vulnerabilities.filter(v => v.severity === severity);
    if (vulnerabilities.length > 0) {
      markdown += `## ${severity} Vulnerabilities\n\n`;
      vulnerabilities.forEach((vuln, index) => {
        markdown += `### ${index + 1}. ${vuln.title}\n`;
        markdown += `- **Severity:** \`${vuln.severity}\`\n`;
        markdown += `- **File:** \`${vuln.file}\`\n\n`;
        markdown += `#### Description\n${vuln.description}\n\n`;
        markdown += `#### Proof of Concept (PoC)\n${vuln.poc}\n\n`;
        markdown += `#### Mitigation Steps\n${vuln.mitigation}\n\n`;
        markdown += `#### Potential Impact\n${vuln.impact}\n\n---\n`;
      });
    }
  });
  return markdown;
};

const generateHTML = (report) => {
    // This HTML generation can be improved as well, but for now, we focus on the dashboard UI
    // The existing HTML generation logic is kept as is.
  const style = `
    body { font-family: sans-serif; padding: 20px; background: #f9f9f9; color: #333; }
    h1, h2, h3 { color: #2c3e50; }
    code, pre { background: #eee; padding: 4px 8px; border-radius: 4px; }
    .badge { display: inline-block; padding: 4px 10px; border-radius: 5px; font-weight: bold; }
    .critical { background: #d32f2f; color: #fff; }
    .high { background: #f57c00; color: #fff; }
    .medium { background: #fbc02d; color: #000; }
    .vuln { border-left: 4px solid #ccc; padding: 10px; margin: 15px 0; background: white; box-shadow: 0 0 10px rgba(0,0,0,0.05); }
  `;
  let html = `<html><head><meta charset="UTF-8"><title>${report.appName} - HTML Report</title><style>${style}</style></head><body>`;
  html += `<h1>Security Vulnerability Report for ${report.appName}</h1>`;
  html += `<p><strong>Scan Date:</strong> ${new Date().toUTCString()}</p>`;
  html += `<p><strong>Package Name:</strong> ${report.packageName}</p><hr/>`;

  ['Critical', 'High', 'Medium'].forEach(sev => {
    const list = report.vulnerabilities.filter(v => v.severity === sev);
    if (list.length) {
      html += `<h2>${sev} Vulnerabilities</h2>`;
      list.forEach((vuln, i) => {
        html += `
          <div class="vuln">
            <h3>${i + 1}. ${vuln.title} <span class="badge ${sev.toLowerCase()}">${sev}</span></h3>
            <p><strong>File:</strong> <code>${vuln.file}</code></p>
            <p><strong>Description:</strong> ${vuln.description}</p>
            <p><strong>Proof of Concept:</strong><pre>${vuln.poc}</pre></p>
            <p><strong>Mitigation:</strong><pre>${vuln.mitigation}</pre></p>
            <p><strong>Impact:</strong><pre>${vuln.impact}</pre></p>
          </div>
        `;
      });
    }
  });

  html += `</body></html>`;
  return html;
};


// --- SVG Icons for a professional look ---
const Icon = ({ path, className = "w-5 h-5" }) => (
  <svg className={className} fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg">{path}</svg>
);

const ShieldIcon = () => <Icon path={<path fillRule="evenodd" d="M10 1.944A11.954 11.954 0 012.166 5C2.056 5.649 2 6.319 2 7c0 5.225 3.34 9.67 8 11.317C14.66 16.67 18 12.225 18 7c0-.682-.057-1.35-.166-2.001A11.954 11.954 0 0110 1.944zM10 3.056l.002.001a9.938 9.938 0 00-7.29 3.033.625.625 0 01-.785-.974 11.438 11.438 0 018.073-3.51.625.625 0 01.67 1.451zm0 13.889C5.165 15.54 3.5 11.53 3.5 7c0-.52.044-1.03.128-1.522a.625.625 0 011.23.364A10.462 10.462 0 005 7c0 4.23 2.11 7.84 6.5 9.375a.625.625 0 01-.5-1.187z" clipRule="evenodd" />} className="w-8 h-8 text-blue-500" />;
const PackageIcon = () => <Icon path={<path d="M10.707 2.293a1 1 0 00-1.414 0l-7 7a1 1 0 001.414 1.414L4 10.414V17a1 1 0 001 1h2a1 1 0 001-1v-2a1 1 0 011-1h2a1 1 0 011 1v2a1 1 0 001 1h2a1 1 0 001-1v-6.586l.293.293a1 1 0 001.414-1.414l-7-7z" />} />;
const CalendarIcon = () => <Icon path={<path fillRule="evenodd" d="M6 2a1 1 0 00-1 1v1H4a2 2 0 00-2 2v10a2 2 0 002 2h12a2 2 0 002-2V6a2 2 0 00-2-2h-1V3a1 1 0 10-2 0v1H7V3a1 1 0 00-1-1zm-3 6a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z" clipRule="evenodd" />} />;
const FlaskIcon = () => <Icon path={<path fillRule="evenodd" d="M5 2a1 1 0 011 1v1.172l2.293-2.293a1 1 0 011.414 0l2 2a1 1 0 010 1.414l-2.293 2.293V14a1 1 0 11-2 0V8.414l-2.293 2.293a1 1 0 01-1.414-1.414l2-2a1 1 0 010-1.414L6 3.172V3a1 1 0 01-1-1zm10 0a1 1 0 011 1v6.414l-2.293-2.293a1 1 0 10-1.414 1.414l2.293 2.293V14a1 1 0 11-2 0V9.172l-2.293 2.293a1 1 0 00-1.414-1.414l2-2a1 1 0 000-1.414L13 3.172V3a1 1 0 011-1z" clipRule="evenodd" />} />;
const WrenchIcon = () => <Icon path={<path d="M13.293 4.293a1 1 0 011.414 0l2 2a1 1 0 010 1.414l-9.5 9.5a1 1 0 01-.39.293l-3 1a1 1 0 01-1.213-1.213l1-3a1 1 0 01.293-.39l9.5-9.5zM12 6l-8.5 8.5 1.793 1.793L14 7.207l-2-2z" />} />;
const WarningIcon = () => <Icon path={<path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.22 3.001-1.742 3.001H4.42c-1.522 0-2.492-1.667-1.742-3.001l5.58-9.92zM10 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />} />;
const CheckCircleIcon = () => <Icon path={<path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />} className="w-16 h-16 text-green-500" />;

// --- The Improved ReportDashboard Component ---
function ReportDashboard({ report }) {
  if (!report) {
    return (
      <div className="flex items-center justify-center h-screen bg-gray-50 dark:bg-gray-900">
        <p className="text-gray-500">Loading report...</p>
      </div>
    );
  }

  const downloadMarkdown = () => {
    const blob = new Blob([generateMarkdown(report)], { type: 'text/markdown' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `report-${report.appName}.md`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const downloadHTML = () => {
    const blob = new Blob([generateHTML(report)], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `report-${report.appName}.html`;
    a.click();
    URL.revokeObjectURL(url);
  };

  const getSeverityStyles = (severity) => {
    switch (severity) {
      case 'Critical': return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-200';
      case 'High': return 'bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200';
      case 'Medium': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200';
    }
  };

  return (
    <div className="bg-gray-50 dark:bg-gray-900 min-h-screen p-4 sm:p-6 lg:p-8">
      <div className="max-w-4xl mx-auto">
        {/* --- Report Header --- */}
        <div className="bg-white dark:bg-gray-800 shadow-sm rounded-lg p-6 border border-gray-200 dark:border-gray-700">
            <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4 border-b border-gray-200 dark:border-gray-700 pb-4 mb-4">
                <h1 className="text-2xl font-bold text-gray-900 dark:text-white flex items-center gap-3">
                    <ShieldIcon />
                    <span>{report.appName} Report</span>
                </h1>
                <div className="flex gap-2">
                    <button onClick={downloadMarkdown} className="px-4 py-2 bg-gray-100 hover:bg-gray-200 dark:bg-gray-700 dark:hover:bg-gray-600 text-gray-800 dark:text-gray-200 rounded-md text-sm font-medium transition-colors">
                        Export Markdown
                    </button>
                    <button onClick={downloadHTML} className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-md text-sm font-medium transition-colors">
                        Export HTML
                    </button>
                </div>
            </div>

            {/* --- Report Metadata --- */}
            <div className="flex flex-col sm:flex-row sm:items-center gap-x-6 gap-y-2 text-sm text-gray-600 dark:text-gray-400">
                <p className="flex items-center gap-2">
                    <PackageIcon />
                    <strong>Package:</strong> {report.packageName}
                </p>
                <p className="flex items-center gap-2">
                    <CalendarIcon />
                    <strong>Scan Date:</strong> {new Date().toLocaleString()}
                </p>
            </div>
        </div>

        {/* --- Vulnerabilities List --- */}
        <div className="mt-8 space-y-6">
          {report.vulnerabilities.length > 0 ? (
            report.vulnerabilities.map((vuln, idx) => (
              <div key={idx} className="bg-white dark:bg-gray-800 rounded-lg shadow-sm border border-gray-200 dark:border-gray-700 overflow-hidden">
                <div className="p-4 flex flex-col sm:flex-row sm:items-center justify-between gap-3 bg-gray-50 dark:bg-gray-800/50 border-b border-gray-200 dark:border-gray-700">
                  <h3 className="text-lg font-semibold text-gray-800 dark:text-white">
                    {vuln.title}
                  </h3>
                  <span className={`text-xs px-3 py-1 rounded-full font-semibold ${getSeverityStyles(vuln.severity)}`}>
                    {vuln.severity}
                  </span>
                </div>
                
                <div className="p-4 md:p-6 space-y-6">
                  <div>
                    <p className="text-sm text-gray-500 dark:text-gray-400"><strong>File:</strong> <code className="bg-gray-100 dark:bg-gray-700 rounded px-1.5 py-0.5 text-gray-800 dark:text-gray-200">{vuln.file}</code></p>
                  </div>
                  <div>
                    <p className="text-gray-700 dark:text-gray-300 leading-relaxed">{vuln.description}</p>
                  </div>
                  
                  <div className="space-y-4">
                    {/* PoC Section */}
                    <div>
                        <h4 className="flex items-center gap-2 text-md font-semibold text-gray-800 dark:text-gray-200"><FlaskIcon /> Proof of Concept (PoC)</h4>
                        <pre className="mt-2 p-3 bg-gray-900 text-gray-300 font-mono text-sm rounded-md overflow-x-auto whitespace-pre-wrap">{vuln.poc}</pre>
                    </div>
                    {/* Mitigation Section */}
                    <div>
                        <h4 className="flex items-center gap-2 text-md font-semibold text-green-700 dark:text-green-400"><WrenchIcon /> Mitigation</h4>
                        <pre className="mt-2 p-3 bg-green-50 dark:bg-green-900/20 text-green-800 dark:text-green-300 font-mono text-sm rounded-md overflow-x-auto whitespace-pre-wrap">{vuln.mitigation}</pre>
                    </div>
                    {/* Impact Section */}
                    <div>
                        <h4 className="flex items-center gap-2 text-md font-semibold text-red-700 dark:text-red-400"><WarningIcon /> Potential Impact</h4>
                        <pre className="mt-2 p-3 bg-red-50 dark:bg-red-900/20 text-red-800 dark:text-red-300 font-mono text-sm rounded-md overflow-x-auto whitespace-pre-wrap">{vuln.impact}</pre>
                    </div>
                  </div>
                </div>
              </div>
            ))
          ) : (
            // --- Empty State: No Vulnerabilities Found ---
            <div className="text-center bg-white dark:bg-gray-800 shadow-sm rounded-lg p-10 border border-gray-200 dark:border-gray-700">
              <CheckCircleIcon />
              <h3 className="mt-4 text-xl font-semibold text-gray-900 dark:text-white">No Vulnerabilities Found</h3>
              <p className="mt-1 text-gray-500 dark:text-gray-400">This application appears to be secure based on the latest scan.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default ReportDashboard;
