import React from 'react';

const generateMarkdown = (report) => {
  let markdown = `# Security Vulnerability Report for ${report.appName}\n\n`;
  markdown += `*   **Scan Date:** ${new Date().toUTCString()}\n`;
  markdown += `*   **Package Name:** ${report.packageName}\n---\n`;

  const severities = ['Critical', 'High', 'Medium'];
  severities.forEach(severity => {
    const vulnerabilities = report.vulnerabilities.filter(v => v.severity === severity);
    if (vulnerabilities.length > 0) {
      markdown += `## ${severity} Vulnerabilities\n\n`;
      vulnerabilities.forEach((vuln, index) => {
        markdown += `### ${index + 1}. ${vuln.title}\n`;
        markdown += `- **Severity:** \`${vuln.severity}\`\n`;
        markdown += `- **File:** \`${vuln.file}\`\n\n`;
        markdown += `#### Description\n${vuln.description}\n\n`;
        markdown += `#### Proof of Concept (PoC)\n${vuln.poc}\n\n`;
        markdown += `#### Mitigation Steps\n${vuln.mitigation}\n\n`;
        markdown += `#### Potential Impact\n${vuln.impact}\n\n---\n`;
      });
    }
  });
  return markdown;
};

const generateHTML = (report) => {
  const style = `
    body { font-family: sans-serif; padding: 20px; background: #f7f7f7; color: #333; }
    h1, h2, h3 { color: #2c3e50; }
    code, pre { background: #eee; padding: 4px 8px; border-radius: 4px; }
    .badge { display: inline-block; padding: 3px 8px; border-radius: 4px; font-weight: bold; }
    .critical { background: #e53935; color: white; }
    .high { background: #fb8c00; color: white; }
    .medium { background: #fdd835; color: black; }
    .vuln { border-left: 4px solid #ccc; padding: 10px; margin: 10px 0; background: white; }
  `;
  let html = `<html><head><meta charset="UTF-8"><title>${report.appName} - HTML Report</title><style>${style}</style></head><body>`;
  html += `<h1>Security Vulnerability Report for ${report.appName}</h1>`;
  html += `<p><strong>Scan Date:</strong> ${new Date().toUTCString()}</p>`;
  html += `<p><strong>Package Name:</strong> ${report.packageName}</p><hr/>`;

  ['Critical', 'High', 'Medium'].forEach(sev => {
    const list = report.vulnerabilities.filter(v => v.severity === sev);
    if (list.length) {
      html += `<h2>${sev} Vulnerabilities</h2>`;
      list.forEach((vuln, i) => {
        html += `
          <div class="vuln">
            <h3>${i + 1}. ${vuln.title} <span class="badge ${sev.toLowerCase()}">${sev}</span></h3>
            <p><strong>File:</strong> <code>${vuln.file}</code></p>
            <p><strong>Description:</strong> ${vuln.description}</p>
            <p><strong>Proof of Concept (PoC):</strong><pre>${vuln.poc}</pre></p>
            <p><strong>Mitigation Steps:</strong><pre>${vuln.mitigation}</pre></p>
            <p><strong>Potential Impact:</strong><pre>${vuln.impact}</pre></p>
          </div>
        `;
      });
    }
  });

  html += `</body></html>`;
  return html;
};

function ReportDashboard({ report }) {
  if (!report) return null;

  const downloadMarkdown = () => {
    const blob = new Blob([generateMarkdown(report)], { type: 'text/markdown' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `report-${report.appName}.md`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  const downloadHTML = () => {
    const blob = new Blob([generateHTML(report)], { type: 'text/html' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `report-${report.appName}.html`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  const getSeverityColor = (severity) => {
    switch (severity) {
      case 'Critical': return 'bg-red-600 text-white';
      case 'High': return 'bg-orange-500 text-white';
      case 'Medium': return 'bg-yellow-400 text-black';
      default: return 'bg-gray-300 text-black';
    }
  };

  return (
    <div className="report-dashboard max-w-4xl mx-auto mt-10 p-6 bg-white dark:bg-gray-900 shadow-xl rounded-2xl">
      <div className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4 mb-6">
        <h2 className="text-2xl font-semibold text-gray-800 dark:text-gray-200">
          üîç Report: <span className="font-bold">{report.appName}</span>
        </h2>
        <div className="flex gap-3">
          <button onClick={downloadMarkdown} className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg text-sm">üìÑ Export Markdown</button>
          <button onClick={downloadHTML} className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg text-sm">üåê Export HTML</button>
        </div>
      </div>

      <p className="text-sm text-gray-500 mb-2">üì¶ Package: <strong>{report.packageName}</strong></p>
      <p className="text-sm text-gray-500 mb-6">üïí Scan Date: {new Date().toLocaleString()}</p>

      <div className="space-y-4">
        {report.vulnerabilities.map((vuln, index) => (
          <details key={index} className={`border-l-4 p-4 rounded-lg shadow-md ${
            vuln.severity === 'Critical' ? 'border-red-600 bg-red-50' :
            vuln.severity === 'High' ? 'border-orange-500 bg-orange-50' :
            'border-yellow-400 bg-yellow-50'
          }`}>
            <summary className="cursor-pointer font-semibold text-lg text-gray-800 flex items-center gap-2">
              <span className={`px-2 py-0.5 rounded-full text-xs font-semibold ${getSeverityColor(vuln.severity)}`}>
                {vuln.severity}
              </span>
              {vuln.title}
            </summary>

            <div className="mt-4 space-y-3 text-sm text-gray-700 dark:text-gray-200">
              <p><strong>Description:</strong> {vuln.description}</p>
              <p><strong>File:</strong> <code className="bg-gray-100 dark:bg-gray-800 px-1 rounded">{vuln.file}</code></p>
              <div><strong>PoC:</strong><pre className="bg-black text-green-400 p-3 rounded">{vuln.poc}</pre></div>
              <div><strong>Mitigation:</strong><pre className="bg-gray-900 text-yellow-300 p-3 rounded">{vuln.mitigation}</pre></div>
              <div><strong>Impact:</strong><pre className="bg-gray-100 dark:bg-gray-800 text-red-600 p-3 rounded">{vuln.impact}</pre></div>
            </div>
          </details>
        ))}
      </div>
    </div>
  );
}

export default ReportDashboard;
